by_contentid_round_selectedchoiceindexes.map:
function (doc) {
        if (doc.type === "Answer") {
          emit([doc.contentId, doc.round, doc.selectedChoiceIndexes], {_rev: doc._rev});
        }
      }

by_contentid_creatorid_round.map:
function (doc) {
        if (doc.type === "Answer") {
          emit([doc.contentId, doc.creatorId, doc.round], {_rev: doc._rev});
        }
      }

by_roomid_contentid_round_result.map:
function (doc) {
        if (doc.type === "Answer" && !doc.hidden) {
          emit([doc.roomId, doc.contentId, doc.round, doc.result], {_rev: doc._rev});
        }
      }

points_by_contentid_round_creatorid.map:
function (doc) {
        if (doc.type === "Answer") {
          emit([doc.contentId, doc.round, doc.creatorId], doc.points);
        }
      }
